import styled from 'styled-components';

import enterImage from '../../assets/enter.svg';
import indexImage from '../../assets/index.svg';

export const Container = styled.div``;

export const Info = styled.div`
  height: 30rem;
  width: calc(90vw - 2rem);

  display: flex;

  background: red;
`;

export const InfoText = styled.h1`
  width: 35rem;
  font-weight: 500;
  font-size: 3.5rem;
`;

export const InfoSeparator = styled.div`
  flex: 1 1 auto;
  background: red;
`;

export const InfoButton = styled.button``;

export const InfoButtonIcon = styled.div``;

export const Image = styled.div`
  height: 30rem;
  width: 15rem;

  background: url(${indexImage});
`;
