  background-size: contain;

  @media screen and (max-width: 780px) {
    min-height: 30rem;
    min-width: 43rem;
  }

  @media screen and (min-width: 781px) and (max-width: 1180px) {
    min-height: 10rem;
    min-width: 43rem;
  }
`;

export const Button = styled.button`
  grid-area: dataIndex;

  height: 5rem;
  width: 24rem;

  margin-top: 2rem;
  margin-left: 3rem;

  display: flex;
  justify-content: center;
  align-items: center;

