import styled from 'styled-components';

import enterImage from '../../assets/enter.svg';
import indexImage from '../../assets/index.png';

export const Container = styled.div`
  display: flex;
`;

export const Info = styled.div``;

export const InfoText = styled.h1`
  min-width: 33rem;

  font-weight: 500;
  font-size: 3.5rem;

  text-align: center;
`;

export const Image = styled.div`
  background: url(${indexImage}) no-repeat center center;
  -webkit-background-size: contain;
  -moz-background-size: contain;
  -o-background-size: contain;
  background-size: contain;

  @media screen and (max-width: 780px) {
    min-height: 30rem;
    min-width: 43rem;
  }

  @media screen and (min-width: 781px) and (max-width: 1180px) {
    min-height: 10rem;
    min-width: 43rem;
  }
`;

export const InfoButton = styled.button`
  height: 5rem;
  min-width: 33rem;

  display: flex;
  justify-content: center;
  align-items: center;

  border-radius: 0.5rem;

  font-weight: 700;
  font-size: 1.6rem;

  background: var(--color-content-button-background);
  color: var(--color-content-button-text);
`;

export const InfoButtonIcon = styled.div`
  height: 3rem;
  width: 2rem;

  margin-left: 2rem;

  background: url(${enterImage}) no-repeat center center;
  -webkit-background-size: contain;
  -moz-background-size: contain;
  -o-background-size: contain;
  background-size: contain;
`;
