import styled from 'styled-components';

import enterImage from '../../assets/enter.svg';
import indexImage from '../../assets/index.svg';

export const Container = styled.div`
  display: grid;
  grid-template-rows: 350px 1fr;
  grid-template-columns: 1fr 1fr;
  grid-template-areas:
    'info imageSE'
    'showData showData';
`;

export const InfoText = styled.h1`
  grid-area: info;
  text-align: left;

  font-weight: 500;
  font-size: 3.5rem;
`;

export const InfoButton = styled.button`
  grid-area: showData;
  align-self: center;
`;

export const InfoButtonIcon = styled.div``;

export const Image = styled.div`
  grid-area: imageSE;
  align-self: center;

  flex: 1 1 auto;

  background: url(${indexImage});
`;
